import Header from './components/Header'
import Sidepanel from './components/Sidepanel'
import MainPage from './components/MainPage'
import { useState, useEffect, useLayoutEffect } from 'react'

function App() {
  const [allData, setData] = useState(null)
  const [currentData, setCurrentData] = useState(null)



  useEffect(()=>{
    console.log("got here")
      const getData = async() =>{
      dataFromServer  = await fetchData()
        setData(dataFromServer[0])
        setCurrentData[dataFromServer]
      }  
      getData()
      //setReadyForRender(true)
    }, [])

    const fetchData = async () => {
      let data = new Array()

      let res = await fetch('http://127.0.0.1:8000/get-matches/austin_fc')
      let specificTeam = {
        id : 'austinfc',
        data : await res.json()
      }
      data.push(specificTeam)

      res = await fetch('http://127.0.0.1:8000/get-matches/guatemala')
      specificTeam = {
        id : 'guatemala',
        data : await res.json()
      }
      data.push(specificTeam)

      res = await fetch('http://127.0.0.1:8000/get-matches/honduras')
      specificTeam = {
        id : 'honduras',
        data : await res.json()
      }
      data.push(specificTeam)

      let defaultData = new Array();
      let defaultMessage = "This is a website used to whatever the fuck I want (change later)"
      defaultData.push(defaultMessage)
      specificTeam = {
        id : 'about',
        data : defaultData
      }

      data.push(specificTeam)
      return data; 
    }

    function onClick(country){
        switch(country){
          case "Guatemala":
            console.log(country)
            console.log(dataFromServer)
              setData(dataFromServer[1])
            break;
          case "Honduras":
            console.log(country)
              setData(dataFromServer[3])
            break
          case "Austin FC":
            console.log(country)
              setData(dataFromServer[0])

            break
          case "About":
            console.log(country)
              setData(dataFromServer[3])
            break
        }
    }

    return (
      <div className="container">
        <Header />
        <div className ="mainLayout">
          <Sidepanel onClick = {onClick} />
          <MainPage information={allData}/>
        </div>
      </div>
    );
}

export default App;
