import Header from './components/Header'
import Sidepanel from './components/Sidepanel'
import MainPage from './components/MainPage'
import { useState, useEffect, useLayoutEffect } from 'react'

function App() {

  const [allData, setData] = useState([])

  useEffect(()=>{
      const getData = async() =>{
        const dataFromServer  = await fetchData()
        setData(dataFromServer[0])
      }

      getData()
    } , [])

    const fetchData = async () => {
      let data = new Array()

      let res = await fetch('http://127.0.0.1:8000/get-matches/austin_fc')
      let specificTeam = {
        id : 'austinfc',
        data : await res.json()
      }
      data.push(specificTeam)

      res = await fetch('http://127.0.0.1:8000/get-matches/guatemala')
      specificTeam = {
        id : 'guatemala',
        data : await res.json()
      }
      data.push(specificTeam)

      res = await fetch('http://127.0.0.1:8000/get-matches/honduras')
      specificTeam = {
        id : 'honduras',
        data : await res.json()
      }
      data.push(specificTeam)

      let defaultData = new Array();
      let defaultMessage = "This is a website used to whatever the fuck I want (change later)"
      defaultData.push(defaultMessage)
      specificTeam = {
        id : 'about',
        data : defaultData
      }

      data.push(specificTeam)
      return data; 
    }


  return (
    <div className="container">
      <Header />
      <div className = "mainLayout"> 
        <Sidepanel />
        <MainPage data ={allData}/>
      </div>
    </div>
  );
}

export default App;
